-- Ejecuta esto en Supabase SQL Editor para crear tablas y registrar profesionales (luego crea usuarios Auth y actualiza professional_id)\ncreate extension if not exists "pgcrypto";\n\ncreate table professionals (\n  id uuid primary key default gen_random_uuid(),\n  name text not null,\n  email text unique\n);\n\ncreate table services (\n  id uuid primary key default gen_random_uuid(),\n  professional_id uuid references professionals(id),\n  name text not null,\n  price integer not null,\n  duration_minutes integer not null\n);\n\ncreate table bookings (\n  id uuid primary key default gen_random_uuid(),\n  professional_id uuid references professionals(id),\n  client_name text,\n  client_contact text,\n  service_id uuid references services(id),\n  time timestamptz,\n  status text default 'PENDIENTE',\n  created_at timestamptz default now()\n);\n\n-- Inserta servicios básicos sin professional_id (puedes asignarlos manualmente después)\n-- Ejemplo (puedes eliminar y en su lugar insertar usando los IDs de professionals creados):\n-- insert into professionals (name, email) values ('Marta Santibáñez','marta@podologiamartita.cl');\n-- insert into services (professional_id, name, price, duration_minutes) values ('<ID_DE_MARTA>','Podología',18000,45);\n